package CoflCore.network;

import javax.net.ssl.*;
import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.net.URL;
import java.security.KeyManagementException;
import java.security.KeyStore;
import java.security.KeyStoreException;
import java.security.NoSuchAlgorithmException;
import java.security.UnrecoverableKeyException;
import java.security.cert.CertificateException;
import java.util.Base64;

/**
 * Utility for HTTPS connections with proper SSL/TLS certificate validation.
 * Embedded keystore with ISRG (Let's Encrypt), Google, and SSL.com root CA certificates.
 * 
 * Reference: https://moddev.nea.moe/https/#bringing-your-own-certificates
 */
public class NetworkUtils {
    
    // Embedded keystore with ISRG (Let's Encrypt), Google, SSL.com, and other root CAs (38.5KB)
    private static final String EMBEDDED_KEYSTORE_B64 = "";;
    
    private static SSLContext sslContext;
    private static SSLSocketFactory sslSocketFactory;
    
    static {
        try {
            // Decode embedded base64 keystore (6.4KB with 5 root CAs)
            byte[] decodedKeystore = Base64.getDecoder().decode(EMBEDDED_KEYSTORE_B64);
            ByteArrayInputStream keystoreStream = new ByteArrayInputStream(decodedKeystore);

            KeyStore keyStore = KeyStore.getInstance("PKCS12");
            keyStore.load(keystoreStream, "changeit".toCharArray());

            TrustManagerFactory tmf = TrustManagerFactory.getInstance("PKIX");
            tmf.init(keyStore);

            sslContext = SSLContext.getInstance("TLSv1.2");
            sslContext.init(null, tmf.getTrustManagers(), new java.security.SecureRandom());
            sslSocketFactory = sslContext.getSocketFactory();
        } catch (KeyStoreException | CertificateException | NoSuchAlgorithmException | KeyManagementException | IllegalArgumentException | IOException e) {
            System.err.println("Failed to initialize SSL context: " + e.getMessage());
            e.printStackTrace();
        }
    }
    
    public static SSLContext getSSLContext() {
        return sslContext;
    }
    
    public static SSLSocketFactory getSSLSocketFactory() {
        return sslSocketFactory;
    }
    
    public static HttpsURLConnection setupConnection(URL url) throws IOException {
        HttpsURLConnection connection = (HttpsURLConnection) url.openConnection();
        
        if (sslSocketFactory != null) {
            connection.setSSLSocketFactory(sslSocketFactory);
        }
        
        connection.setRequestProperty("User-Agent", "SkyCoflMod/1.7.8");
        connection.setConnectTimeout(10000);
        connection.setReadTimeout(10000);
        
        return connection;
    }
    
    public static boolean isSSLInitialized() {
        return sslContext != null && sslSocketFactory != null;
    }
}
